	private int[] cookie = new int[] { 1,2,2,1 };
	
	public static void main(String[] args) {
		Cookie ins = new Cookie();
		System.out.println(ins.solution());
	}

	private int solution() {
		int answer = 0;

		int[] r = new int[cookie.length];
		int[] l = new int[cookie.length];
		int[] d = new int[cookie.length];

		// sum to right
		r[0] = cookie[0];
		for (int i = 1; i < r.length; i++) {
			r[i] = r[i - 1] + cookie[i];
		}
		// sum to left
		l[l.length - 1] = cookie[l.length - 1];
		for (int i = r.length - 2; i >= 0; i--) {
			l[i] = l[i + 1] + cookie[i];
		}
		// check & save as (diff,indexes)
		Map<Integer, List<Integer>> diff = new HashMap<>();
		for (int i = 0; i < cookie.length; i++) {
			int tmp = cookie[i] + r[i] - l[i];
			d[i] = tmp;
			List<Integer> idxes = diff.get(tmp);
			if (idxes == null) {
				idxes = new LinkedList<>();
				diff.put(tmp, idxes);
			}
			idxes.add(i);
			if (tmp == 0) {
				answer = Math.max(answer, r[i]);
			}
		}
		
		// i, j는 사용을 시작하고 마치는 index
		int sub = 0;
		for (int i = 0; i < cookie.length - 1; i++) {
			// 좌측부터 사용 안하는 수를 계산에 반영
			if (i > 0) {
				sub += cookie[i - 1];
			}
			int add = 0;
			for (int j = cookie.length - 1; j > i; j--) {
				// 우측부터 사용 안하는 수를 계산에 반영
				if (j < cookie.length - 1) {
					add += cookie[j + 1];
				}
				List<Integer> idxes = diff.get(-add + sub);
				if (idxes != null) {
					for (Integer idx : idxes) {
						if (idx >= i && idx <= j) {
							//System.out.println(i + " " + j + " " + add + " " + sub + " " + answer);
							answer = Math.max(answer, r[idx] - sub);
						}
					}
				}
			}
		}

		System.out.println(Arrays.toString(cookie));
		System.out.println(Arrays.toString(r));
		System.out.println(Arrays.toString(l));
		System.out.println(Arrays.toString(d));
		System.out.println(diff);
		return answer;
	}
